/*
 *   模型注册器，分发器
 * */

let models = [];

module.exports = function (ID, call) {
    if (typeof (ID) == 'object' || typeof (ID) == 'function') {
        if (ID.router) {
            models.push(ID);
        }
    } else {
        for (var i = 0; i < models.length; i++) {
            if (models[i].router(ID)) {
                return models[i];
            }
        }
        return;
    }
};