/*
 *   服务注册器，分发器
 * */
let services = [];

module.exports = function (ID) {
    if (typeof (ID) == 'object' || typeof (ID) == 'function') {
        if (ID.router) {
           services.push(ID);
        }
    } else {
        for (var i = 0; i < services.length; i++) {
            if (services[i].router(ID)) {
                return services[i];
            }
        }
        return;
    }
};